mapper.insert:

 	SELECT BOARD_SEQ.NEXTVAL FROM DUAL

 INSERT INTO BOARD(    BNO 
					       , TITLE 
					       , CONTENT 
					       , WRITER  )
               VALUES(    #{bno}
              		   , #{title}
              		   , #{content}
              		   , #{writer}  )

mapper.update:
UPDATE BOARD
   SET TITLE=#{title},
   	   CONTENT=#{content}
 WHERE BNO=#{bno}

UPDATE BOARD SET
HIT = HIT+1
WHERE BNO=#{bno}
