mapper.insert:

 	SELECT SCHEDULE_SEQ.NEXTVAL FROM DUAL

 INSERT INTO SCHEDULE(    BNO 
					       , MEMBER
					       , ROUTINE
					       , SCHDATE
					       , START_TIME
					       , END_TIME
					       , ALLDAY
					       , NORPT
					       , DAYRPT
					       , WEEKRPT
					       , MONTHRPT
					       , YEARRPT
					       , MEMO  )
               VALUES(    #{bno}
              		   , #{member}
              		   , #{routine}
              		   , #{schdate}
              		   , #{start_time}
              		   , #{end_time}
              		   , #{allday}
              		   , #{norpt}
              		   , #{dayrpt}
              		   , #{weekrpt}
              		   , #{monthrpt}
              		   , #{yearrpt}
              		   , #{memo}
              		     )
